import React from 'react';

export const Accordian = ({items}) => {
    const renderedItems = items.map(i => {
        return <div>
            <div className= "title active">
                <i className="dropdon icon"></i>
                {i.title}
            </div>
            <div className="content active">
            <p>{i.content}</p>
            </div> 
        </div>
    })

    return (
        <div></div>
    )

}
